/* Funcion de Redondeo, se usa de la siguiente forma

  select dbo.redondeo(cprecio_publico,5,1)        
  Esto redondea cada cinco colones redondeo normal
  
select dbo.redondeo(cprecio_publico,5,2)        
Esto redondea cada cinco colones redondeo hacia arriba

select dbo.redondeo(cprecio_publico,5,3)        
Esto redondea cada cinco colones redondeo hacia abajo

select dbo.redondeo(cprecio_publico,0.50,1)        
Esto redondea cada cincuenta centavos redondeo normal

*/

CREATE FUNCTION [dbo].[Redondeo]
(
@Numero decimal(18,4),
@Factor decimal(18,4),
@ModoRedondeo tinyint /* 1 Normal 2 Arriba 3 Abajo */
)
RETURNS decimal(18,4)
AS
BEGIN

DECLARE @RESULTADO decimal(18,4)
DECLARE @TEMPO decimal(18,4)
DECLARE @DEC DECIMAL(18,4)

If @Factor = 0
   set @RESULTADO = @Numero
Else
   BEGIN
     If @ModoRedondeo = 0
        set @ModoRedondeo = 1   
     SET @TEMPO = @Numero / @Factor
     SET @RESULTADO = FLOOR(@TEMPO)
     SET @DEC = @TEMPO - @RESULTADO 
     IF @ModoRedondeo = 1 AND @DEC >= 0.5
        SET @RESULTADO = @RESULTADO + 1
     IF @ModoRedondeo = 2 AND @DEC > 0
        SET @RESULTADO = @RESULTADO + 1
     SET @RESULTADO = @RESULTADO * @Factor
   END
RETURN (@RESULTADO )
END

