create proc dsgWho
as 
begin
 Declare @spid int, @cuantos int
 declare @sql varchar(100)

 -- Primero mostramos la informaci贸n de los bloqueadores.
 Select * from master.dbo.sysprocesses where blocked<>0 order by waittime
 -- Despu茅s vamos a hacer un DBCC InputBuffer para ver que hacen..
 Declare cr Cursor for select blocked,count(*) from master.dbo.sysprocesses where blocked<>0 group by blocked
 open cr
 fetch next from cr into @spid,@cuantos
 while @@fetch_status=0
  begin
    -- Muestro la informaci贸n de sysprocesses, que puede servir para ver quien es
    select 'Rol'='Bloqueador', * from  master.dbo.sysprocesses where spid=@spid
    -- Escribo alguna informaci贸n para que se pueda perseguir..
    print 'Buffer de ' + cast(@spid as varchar) +' est谩 bloqueando a '+ cast(@cuantos as varchar) +' procesos..'
    set @sql='Dbcc InputBuffer('+ cast(@spid as varchar) +')'
    exec(@sql)
    --Siguiente bloqueador..
    fetch next from cr into @spid,@cuantos
  end
 close cr
deallocate cr
end

