CREATE FUNCTION dsgNumero
-- Parametros
   (@Texto varchar(50), @Largo decimal(4,0))
RETURNS VARCHAR(50)
AS
BEGIN
  DECLARE @LARGO1 DECIMAL(4,0)
  DECLARE @TEXTO2 VARCHAR(40)
  DECLARE @RESULTADO VARCHAR(50)
  SET @TEXTO2 = LTRIM(@TEXTO)
  SET @LARGO1 = LEN(@TEXTO2)
  IF @LARGO1 <= @LARGO
     SET @RESULTADO = REPLICATE('0',@LARGO-@LARGO1) + @TEXTO2
 ELSE
    SET @RESULTADO = LEFT(@TEXTO2,@LARGO)
   RETURN (@RESULTADO )
END





