CREATE PROCEDURE BuscarConsecutivo(@Codigo CHAR(10),@Consecutivo INT = 0 OUTPUT)
AS BEGIN

Declare @ConsecMax decimal
select @ConsecMax = 0
	IF ((SELECT iConsecutivo FROM DBT95 WHERE sCodigo = @Codigo) = NULL) BEGIN
		SELECT @Consecutivo = -1
	END
	ELSE BEGIN
                           SELECT @ConsecMax = iConsecutivoMaximo FROM DBT95 WHERE sCodigo = @Codigo
                           IF @ConsecMax > 0  BEGIN
                                SELECT @Consecutivo = iConsecutivo FROM DBT95 WHERE sCodigo = @Codigo
                                IF @CONSECUTIVO < @ConsecMax BEGIN
    		          UPDATE DBT95 SET iConsecutivo = iConsecutivo + 1 WHERE sCodigo = @Codigo
 	 	          SELECT @Consecutivo = iConsecutivo FROM DBT95 WHERE sCodigo = @Codigo
                                END
                                ELSE BEGIN
                                     SELECT @Consecutivo = -2
                                END
                          END
                          ELSE BEGIN
  		    UPDATE DBT95 SET iConsecutivo = iConsecutivo + 1 WHERE sCodigo = @Codigo
	 	    SELECT @Consecutivo = iConsecutivo FROM DBT95 WHERE sCodigo = @Codigo
                          END
	END
END

