CREATE FUNCTION [dbo].[dsgVentasBodegas]
-- Parametros
(
@ListaBodegas char(100),
@Producto char(15),
@FechaInicial date,
@FechaFinal date
)
RETURNS decimal(18,2)
AS
BEGIN 
DECLARE @RESULTADO decimal(18,2)
begin
set @resultado =(

Select Sum(UnidadesNetas) as UnidadesNetas
FROM
(
Select sum ( Fa01.cCantidad * (1/In04.nFactor_Medida) ) as UnidadesNetas
From IN04 with(nolock),FA00 with(nolock),FA01 with(nolock)
Where ( (FA00.dFecha BETWEEN @FechaInicial 
AND @FechaFinal)
)
AND FA00.sFactura > ''
And FA00.sTipoFactura <> 'AN'
AND FA00.sPedido = FA01.sPedido
AND FA01.sProductoCombo = ''
AND charindex(FA01.sBodega,@ListaBodegas) > 0
AND FA01.sCodigo_Producto = IN04.sCodigo_Producto
AND IN04.sMaster = @Producto 
UNION ALL
Select sum ( -In14.icantidad * (1/In04.nFactor_Medida)) as UnidadesNetas
FROM IN04 with(nolock),IN13 with(nolock),IN14 with(nolock)
WHERE IN13.sCodigo_Movimiento = '05'
AND ((IN13.dFecha_Movimiento BETWEEN @FechaInicial 
AND @FechaFinal )
)
AND IN13.sLlave = IN14.sLlave
AND IN13.bConfirmado > 0
AND charindex(iN13.sBodega_Entra,@ListaBodegas) > 0
AND IN14.sProductoCombo = ''
AND IN14.sCodigo_Producto = IN04.sCodigo_Producto
AND IN04.sMaster = @Producto
) Vista 
) 
end
RETURN (@RESULTADO )
END 

